import { Injectable } from '@angular/core'
import { Observable } from 'rxjs'
import { HttpClient } from '@angular/common/http'
import { map } from 'rxjs/operators'

import { environment } from 'src/environments/environment'
import { ArticlesInterface } from '../interfaces/articles.interface'
import { GetArticleResponseInterface } from '../interfaces/getArticleResponse.interface'
import { Store, select } from '@ngrx/store'
import { isLoggedInSelector } from 'src/app/pages/auth/store/selectors/auth.selectors'

@Injectable()
export class ArticleService {
  loggedIn$!: Observable<boolean>
  fullUrl: string
  constructor(private http: HttpClient, private store: Store) {
    this.fullUrl = ''
  }
  getArticle(title: string): Observable<ArticlesInterface> {
    this.fullUrl =
      environment.baseUrl + environment.getArticleApiUrl + `${title}/`

    return this.http.get<GetArticleResponseInterface>(this.fullUrl).pipe(
      map((response: GetArticleResponseInterface) => {
        return response.article
      }),
    )
  }
  onFavorite(id: number, type: boolean): Observable<{}> {
    const url = environment.baseUrl + environment.favoriteApiUrl + `${id}/`
    if (type === true) {
      console.log(type)
      return this.http.delete<{}>(url).pipe()
    } else {
      console.log(type)
      return this.http.get<{}>(url).pipe()
    }
  }
}

import { Injectable } from '@angular/core'
import { HttpClient } from '@angular/common/http'
import { Observable } from 'rxjs'
import { map } from 'rxjs/operators'

import { environment } from 'src/environments/environment'

import { CurrentUserInterface } from 'src/app/shared/interfaces/currentUser.interface'
import { AuthRegistrationRequestInterface } from '../interfaces/auth.registration.request.interface'
import { LoginRequestInterface } from '../interfaces/auth.login.request.interface'
import { CurrentUserInputInterface } from 'src/app/shared/interfaces/currrentUserInputInterface'
import { AuthUpdateInterface } from '../interfaces/authUpdate.interface'

@Injectable()
export class AuthService {
  constructor(private http: HttpClient) {}

  getUser(response: CurrentUserInterface) {
    return response
  }
  register(
    data: AuthRegistrationRequestInterface,
  ): Observable<CurrentUserInterface> {
    const url = environment.baseUrl + environment.authRegisterApiUrl
    return this.http
      .post<CurrentUserInterface>(url, data)
      .pipe(map(this.getUser))
  }

  login(data: LoginRequestInterface): Observable<CurrentUserInterface> {
    const url = environment.baseUrl + environment.authLoginApiUrl

    return this.http
      .post<CurrentUserInterface>(url, data)
      .pipe(map(this.getUser))
  }
  getCurrentUser(): Observable<CurrentUserInterface> {
    const url = environment.baseUrl + environment.authAutoLoginApiUrl
    return this.http.get<CurrentUserInterface>(url).pipe(map(this.getUser))
  }
  updateCurrentUser(
    data: AuthUpdateInterface,
  ): Observable<CurrentUserInterface> {
    const url = environment.baseUrl + '/usermanagement/profileUpdate/'
    return this.http
      .put<CurrentUserInterface>(url, data)
      .pipe(map(this.getUser))
  }

  logout() {
    const url = environment.baseUrl + environment.authLogoutApiUrl
    return this.http.get(url)
  }
}
/*
 AuthResponseInterface is type being returned
 LoginRequestInterface is type being sent
*/

import { HttpClient, HttpEvent, HttpRequest } from '@angular/common/http'
import { Injectable } from '@angular/core'
import { Observable } from 'rxjs'
import { CommentInterface } from 'src/app/shared/interfaces/comment.interface'
import { environment } from 'src/environments/environment'

@Injectable()
export class CommentService {
  constructor(private http: HttpClient) {}
  getComment(url: string): Observable<CommentInterface> {
    const fullUrl = environment.baseUrl + url
    return this.http.get<CommentInterface>(fullUrl)
  }
  updateComment(
    url: string,
    data: CommentInterface,
  ): Observable<CommentInterface> {
    const fullUrl = environment.baseUrl + url
    return this.http.put<CommentInterface>(fullUrl, { comment: data })
  }
  deleteComment(url: string): Observable<CommentInterface> {
    const fullUrl = environment.baseUrl + url
    return this.http.delete<CommentInterface>(fullUrl)
  }
  postComment(
    url: string,
    data: CommentInterface,
  ): Observable<CommentInterface> {
    const fullUrl = environment.baseUrl + url
    return this.http.post<CommentInterface>(fullUrl, { comment: data })
  }
}

