  ) {
    this.error = undefined
    updates$
      .pipe(ofType(updateCurrentUserSuccessAction), takeUntil(this.destroyed$))
      .subscribe(() => {
        this.edit = !this.edit
      })
    updates$
      .pipe(ofType(updateCurrentUserFailureAction), takeUntil(this.destroyed$))
      .subscribe((error) => {
        //this.error = error.errors.errors
      })
  }
